script:
frappe.ui.form.on('Gate Pass', {
refresh(frm) {
   frm.set_query('transporter_name', () => {
       return {
           filters : {
              'is_transporter': 1
           }
       }
   });
   
   frm.set_query('supplier_name', () => {
       return {
           filters : {
              'is_transporter': 0
           }
       }
   });
   
if (frm.is_new()) {
   frm.set_value('time_of_visit', frappe.datetime.now_time());
}

if (!frm.doc.__unsaved && !frm.is_new()) {
   frm.add_custom_button('Vehicle Weighing', () => {
      let doc = {
          'gate_pass_no': frm.doc.name,
          'vehicle_no': frm.doc.vehicle_no,
          'visitor_name': frm.doc.visitor_name
      }
      frappe.new_doc('Vehicle Weighing', doc);
   }, __('Create'));
}
},
});

frappe.ui.form.on('Sales Order', {
	refresh(frm) {

	  frappe.ui.form.on('Sales Order', {
   oredr_type:function(frm){
       var data = cur_frm.doc.order_type
       console.log(data);
       if(cur_frm.doc.order_type == 'Sales'){
           cur_frm.set_value("naming_series","SO-D-DM2122-.####")
       }
       else if(cur_frm.doc.order_type == 'Export'){
           cur_frm.set_value("naming_series","SAL-EXP-ORD-.YYYY.-")
       }
       else if(cur_frm.doc.order_type == 'Repairing'){
           cur_frm.set_value("naming_series","SAL-REP-ORD-.YYYY.-")
       }
   }
})
	}
})

frappe.ui.form.on('Payment Entry', {
	refresh(frm)
	{
	if(fr.doc.payment_type == "Pay") {
	cur_frm.add_fetch("bank_account", "account", "paid_from");
	}
	else if (fr.doc.payment_type == "Receive"){
	cur_frm.add_fetch("bank_account", "account", "paid_to");
	}
	}
})
